Window.Script:
var globalDong;
var globalPage;

function Tab0_tab1_btn_serachAddress_OnClick(obj)
{
   var dong = this.Tab0.tab1.edt_address.text;
   globalDong = dong;
   globalPage = 1;

   searchAddress(dong,globalPage);

}

function btn_next_OnClick(obj)
{

   globalPage = globalPage + 1;

   searchAddress(globalDong,globalPage);
}

function btn_pre_OnClick(obj)
{   
   if(globalPage > 1){
      globalPage = globalPage - 1;
   }
   searchAddress(globalDong,globalPage);

}



function Tab0_tab1_Grid0_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
   var addressType = this.Tab0.tab1.cbo_addressType.Text;

   if(addressType == "지번"){
      parent.detailRegistrationTab.tab1.baseAddress.text = gds_post.GetColumn(nRow,"RN_ADRES");
   }else{
      parent.detailRegistrationTab.tab1.baseAddress.text = gds_post.GetColumn(nRow,"LNM_ADRES");
   }
   parent.detailRegistrationTab.tab1.zipCode .text = gds_post.GetColumn(nRow,"ZIP_NO");
   parent.detailRegistrationTab.tab1.detailAddress.text = "";
   close();
}


function searchAddress(dong, page){
   http.Sync="true";
   Transaction("findPostList","svcBase::postList.do?method=findPostList","","gds_post=gds_post","dong='"+dong+"' page='"+globalPage+"'","callback");
   http.Sync="false";
}
