Window.Script:
var selEmpCode;
var selPositionCode;
var empFilename;

var sEmpCode;
var sEmail;

//이미지 불러오기 & 등록
var imgFile;
var imgFileOpen;
var empImg;


function detailRegistrationForm_OnLoadCompleted(obj)
{
	http.Sync=true;
	//전체 사원 목록 받아 옴(그사이 사원추가 등록 했을 경우..대비)
	transaction("findEmployeeList","svcBase::/employee.do?method=findEmployeeList","","dsEmployee=dsEmployee","","callback"); 
	//부서목록 가져오기 -> 로드할 때 부서 목록 다 받아옴 따로 조회 필요 없음 (단. 사원의 경우 퇴직자 선택 할 경우 때문에 조회 버튼 있음)
	transaction("findDeptList","svcBase::/department.do?method=findDeptList","","dsDepartment=dsDepartment","","callback");
	//직급List가져옴
	transaction("findPositionList","svcBase::/position.do?method=findPositionList","","dsPosition=dsPosition","","callback");
	//코드 목록도 얻어 옴
	transaction("findCodeList","svcBase::/code.do?method=findCodeList","","dsCode=dsCode dsDetailCode=dsDetailCode","","callback");

	//호봉 정보를 가져옴 
	transaction("findHobongList","svcHrCircumstance::/hobong.do?method=findHobongList","","dsHobong=dsHobong","","callback");

	//소득세율 4대보험 부담률 받아 옴 
	transaction("findDeductionsInsuranceList",
				"svcHrCircumstance::/deductionInsurance.do?method=findDeductionInsuranceList",
				"",
				"dsDeductionTax=dsDeductionTax dsIncomeTax=dsIncomeTax",
				"",
				"callback");
	//일단 상세테이블 6개 받아옴

	transaction("findEmployeeInfoAll",
		"svcHrPM::/register.do?method=findEmployeeInfoAll",
		"",
		"dsEmpInfo=dsEmpInfo dsFamilyInfo=dsFamilyInfo dsLicenseInfo=dsLicenseInfo dsWorkInfo=dsWorkInfo dsSalInfo=dsSalInfo dsEducationInfo=dsEducationInfo",
		"",
		"callback"
	);

	//받아 오자마자 아무도 세팅 을 안한다.!(reload 될 때 아무도 없게 하기 위해서 넣어 줌)	
	dsEmpInfo.Filter("EMP_CODE='"+0+"'");
	dsWorkInfo.Filter("EMP_CODE='"+0+"'");
	dsFamilyInfo.Filter("EMP_CODE='"+0+"'");
	dsLicenseInfo.Filter("EMP_CODE='"+0+"'");
	dsEducationInfo.Filter("EMP_CODE='"+0+"'");
	dsSalInfo.Filter("EMP_CODE='"+0+"'");
	dsPosition.Filter("POSITION_CODE='"+0+"'");


	dsHobongCopy.Copy(dsHobong); // 호봉정보 담는다. 
	dsHobongCopy.Filter("POSITION_CODE='"+0+"'"); // 초기에 값이 없도록 


	dsTempDepartment.Copy("dsDepartment");
	dsTempPosition.Copy("dsPosition");

	//이미지 등록 시!!
	imgFile=detailRegistrationTab.tab1.imgFile;
	imgFileOpen=detailRegistrationTab.tab1.imgFileOpen;
	empImg=detailRegistrationTab.tab1.empImg;

	//전체 detailCode를 filter사용 값을 제한 적으로 담음
	detailCodeFilter();

	http.Sync=false;
}


function deptSerchCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	http.Sync=true;	
	//alert(strCode);

	dsEmployee.filter("DEPT_CODE='"+strCode+"'");
	dsTempEmployee.CopyF(dsEmployee);

	http.Sync=false;
}




// 사진 찾기 
function detailRegistrationTab_tab1_findImgBtn_OnClick(obj)
{
	http.Sync=true;
	//파일 불러올 dialog창 부름
	imgFileOpen.open();
	//dialog.filepath -> 선택된 경로... (마지막 / 까지 \\ \/ 넣는 것.. 경로/선택한 파일명)
	var fileName=imgFileOpen.FilePath + "\\" + imgFileOpen.FileName;

	alert("fileName"+fileName); //-> 파일 경로가 뜬다.

	imgFile.FileName=fileName; // file에도 이름을 넣고 

	empImg.ImageID=fileName;      // Img에도 이름을 넣어 준다.(표시할 이미지 )
	empImg.FillType="STRETCH";   //이미지를 영역에 맞도록 늘려 준다.
	http.Sync=false;
}

//가장 많은 것을 할 것 !! 부서 선택 후 사원을 클릭 할 경우 받아온다.(뜨는거 봐서 더블클릭이 나으면 그것으로 )
function detailEmpGrid_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//선택한 사원의 이름 사원크드 직급코드 담는다.
	var selEmpName = dsEmployee.getColumn(nRow, "EMP_NAME");
	selEmpCode = dsEmployee.getColumn(nRow, "EMP_CODE");
	selPositionCode = dsEmployee.GetColumn(nRow,"POSITION_CODE");

	//선택한 사원의 상세정보가 뜬다!
	dsEmpInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsWorkInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsFamilyInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsLicenseInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsEducationInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsSalInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsHobongCopy.Filter("POSITION_CODE='"+selPositionCode+"' && HOBONG='"+dsSalInfo.GetColumn(dsEmployee.rowpos,"HOBONG")+"'");
	dsPosition.Filter("POSITION_CODE='"+selPositionCode+"'");


	if(detailRegistrationTab.tab3.selHobong.Text!=""){  // 신규 사원이나 호봉이 등록 되어 있지 않는 사원의 초기 값
		detailRegistrationTab.tab3.baseSal.Text=dsHobongCopy.GetColumn(0,"BASE_SAL");
		detailRegistrationTab.tab3.bonus.Text=dsHobongCopy.GetColumn(0,"BONUS");
		detailRegistrationTab.tab3.sigub.Text=dsHobongCopy.GetColumn(0,"SIGUB");
	}else{
		detailRegistrationTab.tab3.baseSal.Text="";
		detailRegistrationTab.tab3.bonus.Text="";
		detailRegistrationTab.tab3.sigub.Text="";
	}
	//alert("사원 사진 정보 있으면 사진 불러 옵니다.!! [일단 주석 처리]");

	//[사원의 사진 정보 가져 오는 곳!!]
	var empImgName=dsEmpInfo.getColumn(dsEmpInfo.rowpos,'IMG_SRC');

	//alert("empImgName-----"+empImgName);

	if(Length(empImgName)!=0){	
      empImg.ImageID="http://localhost:8282/test4th/img/"+empImgName+"?time="+timem();   
      empImg.FillType="STRETCH";  
	}else{
      alert("이미지 호출 실패!");
      empImg.ImageID="http://localhost:8282/test4th/img/noimage.jpg?time="+timem();
    } 

}


