}

// 기본 Dataset에 code에 filter를 사용 값을 제한 적으로 담음 
function detailCodeFilter(){
	//내국인 외국인 조사
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'NA'");
	dsNativeType.CopyF(dsDetailCode);

	//병역 사항
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'DI'");
	dsDisabilityType.CopyF(dsDetailCode);

	//고용형태 
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,3) == 'EMT'");
	dsEmployeementType.CopyF(dsDetailCode);


	//직종 
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'JJ'");
	dsJikjong.CopyF(dsDetailCode);


	//프로젝트
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'PJ'");
	dsProject.CopyF(dsDetailCode);

	//가족관계 
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'RL'");
	dsRelationship.CopyF(dsDetailCode);

	//최종학력 (x없는 듯. )
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'ED'");
	dsHighestEducation.CopyF(dsDetailCode);

	//학교 선택
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'UN'");
	dsCampusType.CopyF(dsDetailCode);

	//급여형태 
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'PA'");
	dsSalaryType.CopyF(dsDetailCode);

	/*

	//일단 자격증 안쓰는듯.

	//자격증 유무 
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'LI'");
	dsLicenseType.CopyF(dsDetailCode);

	//발행처 
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,2) == 'LC'");
	dsLicenseCenter.CopyF(dsDetailCode);



	//연금유형 
	dsDetailCode.Filter("SubStr(DETAIL_CODE,0,3) == 'PEC'");
	dsPensionType.CopyF(dsDetailCode);
	*/

}


