//사진 저장!!(웹로직 이용)
function detailRegistrationTab_tab1_saveImgBtn_OnClick(obj)
{
	http.Sync=true;
	//empFilename=imgFileOpen.Filename;   


	// 사원 번호로 파일 이름을 만든다.[추가부분]
	var fileExtend = SubStr(imgFileOpen.fileName, IndexOf(imgFileOpen.fileName,"."));



	empFileName =  selEmpCode + fileExtend;

	//alert(empFileName);//확장자 얻어 옴 .jpg 등 
	// 사원 선택 해서 들어 오면 여기 받아온다 사원 번호


	//여기까지 이름변경 해 보려고[추가부분]

	imgFile.Open("rb");

    var empImgFile=imgFile.ReadBinary(); //Binary 형식으로 읽기

	dsImg.AddRow();  // file 이름과 data를 넣을 dataset 에 row를 추가함

	//이미지 데이터 셋에. 
	dsImg.SetColumn(dsImg.GetCurrow(),"EMP_FILE_NAME",empFilename);
	dsImg.SetColumn(dsImg.GetCurrow(),"IMG_FILE_DATA",empImgFile);
	//사원상세 정보의 이미지 경로를 창에 이미지 이름을 저장해 둔다.
	dsEmpInfo.SetColumn(dsEmpInfo.row,"IMG_SRC",empFilename);
	//저장되는 이름을 확인!!
	alert("사진경로:::"+empFilename);
	//이미지 저장 메서드 호출 !!
	//alert("웹로직 할 때 생성 되는지 확인 가능 함");
	Transaction("saveEmpImg","svcHrPM::/register.do?method=saveEmpImg","dsImg=dsImg:u","","","callback");
	//웹로직 연결시 생성되는지 확인 가능 [나중에 할 것!!]
	http.Sync=false;
}


//일괄 저장[수정 완료 후 저장 버튼]
function detailRegistrationTab_tab1_batchBtn_OnClick(obj)
{

	if(confirm("저장 하시겠습니까?")){

	//alert("저장하기 버튼");
	//우선 사원 정보만 수정 함.
		transaction(
			"batchEmployeeInfoAll",
			"svcHrPM::/register.do?method=batchEmployeeInfoAll",
			"dsEmpInfo=dsEmpInfo:u dsWorkInfo=dsWorkInfo:u dsFamilyInfo=dsFamilyInfo:u dsLicenseInfo=dsLicenseInfo:u dsEducationInfo=dsEducationInfo:u dsSalInfo=dsSalInfo:u" ,
			"dsEmpInfo=dsEmpInfo dsWorkInfo=dsWorkInfo dsFamilyInfo=dsFamilyInfo dsLicenseInfo=dsLicenseInfo dsEducationInfo=dsEducationInfo dsSalInfo=dsSalInfo" ,
			"",
			"callback");	
	}
	detailRegistrationTab.tab3.autoResultBtn.visible=false;
	Reload();
}




