// 호봉 선택 시 
function detailRegistrationTab_tab3_selHobong_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	dsHobong.UnFilter();
	dsHobong.Filter("POSITION_CODE='"+selPositionCode+"' && HOBONG='"+strText+"'");
	if(dsHobong.GetRowCount()==0){
		alert(" dsHobong.GetRowCount()==0:( ");
		detailRegistrationTab.tab3.baseSal.Text="";
		detailRegistrationTab.tab3.bonus.Text="";
		detailRegistrationTab.tab3.sigub.Text="";
	}else{
		detailRegistrationTab.tab3.baseSal.Text=dsHobong.GetColumn(0,"BASE_SAL");
		detailRegistrationTab.tab3.bonus.Text=dsHobong.GetColumn(0,"BONUS");
		detailRegistrationTab.tab3.sigub.Text=dsHobong.GetColumn(0,"SIGUB");
	}
}

