// 장기 요양 보험 계산 
function detailRegistrationTab_tab3_autoResultBtn_OnClick(obj)
{
	if(detailRegistrationTab.tab3.healthInsur.text ==""){
      alert("건강 보험을 먼저 계산 하셔야 합니다. ");   
   }else{
      dsDeductionTax.Filter("INPUTED_YEAR='"+substr(today(),0,4)+"'");
      var longTermCareRate=dsDeductionTax.getColumn(dsDeductionTax.row,"LONG_TERM_CARE_RATE"); // 장기 요양 보험률 가져 옴
      var healthInsurSal=StripNum(detailRegistrationTab.tab3.healthInsur.text);
      var resultLongTermCareSal=healthInsurSal * ParseFloat(longTermCareRate) / 100;
      detailRegistrationTab.tab3.longTermCare.text=Round(resultLongTermCareSal,0);
   }
}

function detailEmpGrid_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	//선택한 사원의 이름 사원크드 직급코드 담는다.
	var selEmpName = dsEmployee.getColumn(nRow, "EMP_NAME");
	selEmpCode = dsEmployee.getColumn(nRow, "EMP_CODE");
	selPositionCode = dsEmployee.GetColumn(nRow,"POSITION_CODE");

	//선택한 사원의 상세정보가 뜬다!
	dsEmpInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsWorkInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsFamilyInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsLicenseInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsEducationInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsSalInfo.Filter("EMP_CODE='"+selEmpCode+"'");
	dsHobongCopy.Filter("POSITION_CODE='"+selPositionCode+"' && HOBONG='"+dsSalInfo.GetColumn(dsEmployee.rowpos,"HOBONG")+"'");
	dsPosition.Filter("POSITION_CODE='"+selPositionCode+"'");


	if(detailRegistrationTab.tab3.selHobong.Text!=""){  // 신규 사원이나 호봉이 등록 되어 있지 않는 사원의 초기 값
		detailRegistrationTab.tab3.baseSal.Text=dsHobongCopy.GetColumn(0,"BASE_SAL");
		detailRegistrationTab.tab3.bonus.Text=dsHobongCopy.GetColumn(0,"BONUS");
		detailRegistrationTab.tab3.sigub.Text=dsHobongCopy.GetColumn(0,"SIGUB");
	}else{
		detailRegistrationTab.tab3.baseSal.Text="";
		detailRegistrationTab.tab3.bonus.Text="";
		detailRegistrationTab.tab3.sigub.Text="";
	}
	//alert("사원 사진 정보 있으면 사진 불러 옵니다.!! [일단 주석 처리]");

	//[사원의 사진 정보 가져 오는 곳!!]
	var empImgName=dsEmpInfo.getColumn(dsEmpInfo.rowpos,'IMG_SRC');

	//alert("empImgName-----"+empImgName);

	if(Length(empImgName)!=0){	
      empImg.ImageID="http://localhost:8282/test4th/img/"+empImgName+"?time="+timem();   
      empImg.FillType="STRETCH";  
	}else{
      //alert("이미지 호출 실패!");
      empImg.ImageID="http://localhost:8282/test4th/img/noimage.jpg?time="+timem();
    }	
}



//재직증명서 발급 







function Button0_OnClick(obj)
{
	sEmpCode=dsTempEmployee.GetColumn(dsTempEmployee.row, "EMP_CODE");
   //sEmpCode=dsTempEmployee.GetColumn(nRow, "EMP_CODE");
   trace("@@@@"+sEmpCode);

   transaction("findEmployeeList",
            "svcBase::/employee.do?method=pdfSend",
            "",
            "dsEmployee=dsEmployee",
            "sEmpCode='"+sEmpCode+"'",
            "callback");
   Dialog("base::pdfForm.xml","");


}

function Button1_OnClick(obj)
{
	sEmpCode=dsTempEmployee.GetColumn(dsTempEmployee.row, "EMP_CODE");
   sEmail=dsEmpInfo.GetColumn(dsEmpInfo.row,"EMAIL");
   trace("@@@@!!"+sEmpCode);
   if(Length(sEmpCode)==0){
      alert("사원을 선택하세요.");

   }else{
      transaction("findEmployeeList",
               "svcBase::/employee.do?method=sendEmail",
               "",
               "dsEmployee=dsEmployee",
               "sEmpCode="+Quote(sEmpCode)+" sEmail="+Quote(sEmail),
               "callback");
      alert("메일전송을 완료 했습니다.");
   }
}
