mapper.select:
SELECT CONTRACT_NO, ESTIMATE_NO, CONTRACT_TYPE, CUSTOMER_CODE , CONTRACT_DATE, CONTRACT_REQUESTER, PERSON_CODE_IN_CHARGE , DESCRIPTION, DELIVERY_RESULT_STATUS,SLIP_REGIST_STATUS FROM CONTRACT
WHERE CONTRACT_DATE BETWEEN #{fromDate} AND #{toDate}
WHERE DELIVERY_RESULT_STATUS IS NULL OR DELIVERY_RESULT_STATUS ='N'

mapper.insert:
INSERT INTO CONTRACT(
CONTRACT_NO
, ESTIMATE_NO
, CONTRACT_TYPE
, CUSTOMER_CODE
, CONTRACT_DATE
,CONTRACT_REQUESTER
, PERSON_CODE_IN_CHARGE
, DESCRIPTION
,DELIVERY_RESULT_STATUS)
VALUES
( #{contractNo}
, #{estimateNo}
, #{contractType}
, #{customerCode}
,
#{contractDate}
, #{contractRequester}
, #{personCodeInCharge}
,#{description}
, #{deliveryResultStatus}
)

mapper.update:
UPDATE CONTRACT SET
CONTRACT_TYPE = #{contractType}
, CUSTOMER_CODE = #{customerCode}
,
CONTRACT_DATE = #{contractDate}
, CONTRACT_REQUESTER =
#{contractRequester}
, PERSON_CODE_IN_CHARGE = #{personCodeInCharge}
,
DESCRIPTION = #{description}
, DELIVERY_RESULT_STATUS =
#{deliveryResultStatus}
,SLIP_REGIST_STATUS =
#{slipRegistStatus}
WHERE
CONTRACT_NO = #{contractNo}
AND
ESTIMATE_NO =
#{estimateNo}
