mapper.insert:
INSERT INTO ESTIMATE_DETAIL(
  ESTIMATE_DETAIL_NO
, ESTIMATE_NO
, ITEM_CODE
, ITEM_NAME
, UNIT_OF_ESTIMATE
, DUE_DATE_OF_ESTIMATE
, ESTIMATE_AMOUNT
, UNIT_PRICE_OF_ESTIMATE
, SUM_PRICE_OF_ESTIMATE
, DESCRIPTION
)
VALUES
( #{estimateDetailNo}
, #{estimateNo}
, #{itemCode}
, #{itemName}
, #{unitOfEstimate}
, #{dueDateOfEstimate}
, #{estimateAmount}
, #{unitPriceOfEstimate}
, #{sumPriceOfEstimate}
, #{description}
)

mapper.update:
UPDATE ESTIMATE_DETAIL SET
ESTIMATE_DETAIL_NO = #{estimateDetailNo}
,ESTIMATE_NO = #{estimateNo}
,ITEM_CODE = #{itemCode}
,ITEM_NAME = #{itemName}
,UNIT_OF_ESTIMATE =
#{unitOfEstimate}
,DUE_DATE_OF_ESTIMATE = #{dueDateOfEstimate}
,ESTIMATE_AMOUNT = #{estimateAmount}
,UNIT_PRICE_OF_ESTIMATE =
#{unitPriceOfEstimate}
,SUM_PRICE_OF_ESTIMATE = #{sumPriceOfEstimate}
,DESCRIPTION = #{description}
WHERE ESTIMATE_DETAIL_NO =
#{estimateDetailNo}

mapper.delete:
DELETE FROM ESTIMATE_DETAIL
WHERE ESTIMATE_DETAIL_NO =
#{estimateDetailNo}
AND ESTIMATE_NO = #{estimateNo}
