mapper.insert:
INSERT INTO CONTRACT_DETAIL(
  CONTRACT_DETAIL_NO
, CONTRACT_NO
, ITEM_CODE
, ITEM_NAME
, UNIT_OF_CONTRACT
, DUE_DATE_OF_CONTRACT
, CONTRACT_AMOUNT
, UNIT_PRICE_OF_CONTRACT
, SUM_PRICE_OF_CONTRACT
, MPS_APPLY_STATUS
, DELIVERY_STATUS
, DESCRIPTION
, DELIVERY_REMAIN
)
VALUES
( #{contractDetailNo}
, #{contractNo}
, #{itemCode}
, #{itemName}
, #{unitOfContract}
, #{dueDateOfContract}
, #{contractAmount}
, #{unitPriceOfContract}
, #{contractAmount}*#{unitPriceOfContract}
, #{mpsApplyStatus}
, #{deliveryStatus}
, #{description}
, #{deliveryRemain}
)

mapper.update:
UPDATE CONTRACT_DETAIL SET
ITEM_CODE = #{itemCode}
, ITEM_NAME = #{itemName}
, UNIT_OF_CONTRACT = #{unitOfContract}
, DUE_DATE_OF_CONTRACT = #{dueDateOfContract}
, CONTRACT_AMOUNT = #{contractAmount}
, UNIT_PRICE_OF_CONTRACT = #{unitPriceOfContract}
, SUM_PRICE_OF_CONTRACT = #{sumPriceOfContract}
, MPS_APPLY_STATUS = #{mpsApplyStatus}
, DELIVERY_STATUS =	#{deliveryStatus}
, DESCRIPTION = #{description}
,DELIVERY_REMAIN = #{deliveryRemain}
,SLIP_REGIST_STATUS = #{slipRegistStatus}
WHERE
CONTRACT_DETAIL_NO =
#{contractDetailNo}
AND
CONTRACT_NO = #{contractNo}

mapper.delete:
DELETE FROM CONTRACT_DETAIL
WHERE CONTRACT_DETAIL_NO =
#{contractDetailNo}
AND CONTRACT_NO= #{contractNo}
