mapper.select:
SELECT MPS_NO, MPS_PLAN_CLASSIFICATION, CONTRACT_DETAIL_NO, SALES_PLAN_NO, ITEM_CODE, ITEM_NAME, UNIT_OF_MPS, MPS_PLAN_DATE, MPS_PLAN_AMOUNT, DUE_DATE_OF_MPS, SCHEDULED_END_DATE, MRP_APPLY_STATUS, DESCRIPTION FROM MPS
WHERE DUE_DATE_OF_MPS BETWEEN #{fromDate} AND #{toDate}
WHERE MRP_APPLY_STATUS IS NULL OR MRP_APPLY_STATUS='N'

mapper.insert:
INSERT INTO MPS(
MPS_NO
, MPS_PLAN_CLASSIFICATION
, CONTRACT_DETAIL_NO
, SALES_PLAN_NO
, ITEM_CODE
, ITEM_NAME
, UNIT_OF_MPS
, MPS_PLAN_DATE
, MPS_PLAN_AMOUNT
, DUE_DATE_OF_MPS
, SCHEDULED_END_DATE
, MRP_APPLY_STATUS
, DESCRIPTION
)
VALUES
( #{mpsNo}
, #{mpsPlanClassification}
, #{contractDetailNo}
, #{salesPlanNo}
, #{itemCode}
, #{itemName}
, #{unitOfMps}
, #{mpsPlanDate}
, #{mpsPlanAmount}
, #{dueDateOfMps}
, #{scheduledEndDate}
, #{mrpApplyStatus}
, #{description}
)
