mapper.insert:
SELECT PAY_DEDUCTION_ITEM_SEQ.NEXTVAL FROM DUAL
INSERT INTO PAY_DEDUCTION_MGT VALUES ( #{payDeductionItemSeq}, #{payTypeCode}, #{payDeductionTypeCode}, #{inputedYear}, #{payDeductionItemCode}, #{payDeductionItemName}, #{taxTypeCode}, #{susubApply}, #{monthSal}, #{price}, #{inOutApply}
)

mapper.delete:

      DELETE FROM
      		PAY_DEDUCTION_MGT
      WHERE
            PAY_DEDUCTION_ITEM_SEQ = #{payDeductionItemSeq}
      AND
       		INPUTED_YEAR=#{inputedYear}
      AND
       		PAY_TYPE_CODE=#{payTypeCode}
      AND
			PAY_DEDUCTION_TYPE_CODE=#{payDeductionTypeCode}
